import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';

import Profile from '../pages/Profile';





//


const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 700,
  bgcolor: 'background.paper',
  border: '1px solid #fff',
  boxShadow: 50,
  p: 4,
};





export default function TrioModal() {



  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

//

const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    const blog = { firstName };

    fetch('http://localhost:8000/verseny/', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      // history.go(-1);
      navigate.push('/');
    })
  }

  const { user } = useOutletContext();

  const [firstName, setFirstName] = useState(user?.metadata?.firstName ?? '');



  return (
    <div>
      <Button
       size="large"
       variant="outlined" color="error"
      onClick={handleOpen}>Trio</Button>
      <Modal
        open={open}
        onClose={handleClose}
        herf= "versenyek/index.htmel"
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
        
      >
        <Box sx={style}>


        <div>
      <h2>Add a New Blog</h2>
      <form onSubmit={handleSubmit}>
        
        <StepLabel>Blog author:</StepLabel>
        <select
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        >
          <option value={user?.metadata?.firstName }>{user?.metadata?.firstName }</option>
          <option value="yoshi">yoshi</option>
        </select>
        <Button >Csatlakozzas</Button>
      </form>
    </div>
        
        



        </Box>
      </Modal>
    </div>
  );
}
