import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useOutletContext } from 'react-router-dom';
import  Button  from '@mui/material/Button';
import  StepLabel from '@mui/material/StepLabel';
import Typography  from '@mui/material/Typography';
import Select from '@mui/material/Select';


const Create = () => {
 

  
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    const blog = { firstName };

    fetch('http://localhost:8000/verseny/', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      // history.go(-1);
      navigate.push('/');
    })
  }

  const { user } = useOutletContext();

  const [firstName, setFirstName] = useState(user?.metadata?.firstName ?? '');


  return (
    <div>
      <Typography variant="h3" color='error'>Jelentkez egy versenyre:</Typography>
      <form onSubmit={handleSubmit}>
        
        <StepLabel>Blog author:</StepLabel>
        <Select
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        >
          <option value={user?.metadata?.firstName }>{user?.metadata?.firstName }</option>
        </Select>
        <Button >Csatlakozzas</Button>
      </form>
    </div>
  );
}
 
export default Create;
